<script>
    $(function() {
        var size = 1;
        $('.easter').click(function() {

            size += 3;
            $(this).children().css({
                transform: 'scale(' + size + ')',
                transition: 'all 0.4s ease'
            });
            
            setTimeout(function() {
                $('.easter').children().css({
                    transform: 'scale(1)',
                    transition: 'all 0.5s ease-in-out'
                });
            }, 500);

            // console.log(size);

            if (size > 15) {
                $('.easter').children().css({
                    color: '#000',
                    transform: 'scale(999)',
                    transition: 'all 0.5s ease-in-out',
                });

                $('.easter').hide();
                $('#particle-slider').show('fast');
                $('.nav-header').hide();
                $('.aboutInfo').hide();

                $('html, body, .particle').css({
                    backgroundImage: 'none',
                    backgroundColor: 'black',
                    width: '100%',
                    height: '100%',
                    margin: 0,
                    padding: 0,
                    overflow: 'hidden',
                    transition: 'all 0.5s ease'
                });
            }
        });

    });

    function first() {
        setTimeout(function() {
            $('.easter').children().css({
                transform: 'scale(999)',
                transition: 'all 0.5s ease-in-out',
                opacity: '0',
            });
        }, 250)
    }

    function second() {
        setTimeout(function() {
            $('.easter').children().css({
                position: 'none',
            });
        }, 250)
    }
</script>